"""
Các hàm tiện ích để loại bỏ các bản ghi trùng lặp trong dữ liệu.
"""
import pandas as pd

def drop_duplicates(df: pd.DataFrame, subset: list = None) -> pd.DataFrame:
    """
    Loại bỏ các dòng trùng lặp trong DataFrame dựa trên các cột xác định.
    
    :param df: DataFrame cần xử lý.
    :param subset: Danh sách các cột để kiểm tra trùng lặp. Nếu None, kiểm tra tất cả các cột.
    :return: DataFrame đã loại bỏ các bản ghi trùng lặp.
    """
    return df.drop_duplicates(subset=subset).reset_index(drop=True)
